import React from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import { THEME } from "../THEME";
import { Entypo } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';

export const StartScreen = () => {
  return (
    <View style={styles.container}>
      <View style={styles.buttonWrapper}>
        <TouchableOpacity style={styles.btn}>
          <View  style={styles.icon}>
            <Entypo name="arrow-with-circle-right" size={24} color="white"
              />
          </View>
          <Text style={styles.text}>Шифровать</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.btn}>
          <Entypo name="arrow-with-circle-left" style={styles.icon} size={24} color="white" />
          <Text style={styles.text}>Разшифровать</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.btn}>
          <MaterialCommunityIcons name="settings" style={styles.icon} size={24} color="white" />
          <Text style={styles.text}>Настойки</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.btn}>
          <AntDesign name="exclamationcircleo" size={24} style={styles.icon} color="white" />
          <Text style={styles.text}>О приложении sdfsdfsfsdf sdfsdfsdf</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: THEME.bg_color,
    alignItems: 'center',
    justifyContent: 'space-around',
    width: "100%",
  },
  btn: {
    marginVertical: 8,
    marginHorizontal: 30,
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 20,
    backgroundColor: "blue",
    borderRadius: 20,
  },
  text: {
    color: "white",
    fontSize: 24,
    width:'81%',
    backgroundColor:'red',
    textAlign:'centr'
  },
  buttonWrapper: {
    backgroundColor: "green",
    width: "100%",
  },
  icon: {
    width:'18%',
    backgroundColor:'red',

  }
});
