import { KEY, TEXT,ENCRIPT_TEXT,START_ENCRIPT_TEXT,DE_ENCRIPT_TEXT } from "../types";

const initialState = {
    startText:'Start Text',
    encriptText:'Finish Text',
    key:'Key',
    askiiCod:`'!"#$%&()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\]^_abcdefghijklmnopqrstuvwxyz{|}~
    АБВГДЕЖЗИЙКЛМНОПРСТУФХЦЧШЩЪЫЬЭЮЯабвгдежзийклмнопрстуфхцчшщъыьэюя`,
    deEncriptText:'deFin',
};
export const mainReducer = (state = initialState, action) => {
    switch(action.type){  
        case KEY:return{...state, key:action.payload  } 
        case TEXT:return{...state, startText:action.payload  } 
        case ENCRIPT_TEXT:return{...state, encriptText:action.payload  } 
        case DE_ENCRIPT_TEXT:return{...state, deEncriptText:action.payload  } 
        default:return state
    }
  return state;
};
