import { StatusBar } from "expo-status-bar";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Clipboard,
  StyleSheet,
  Text,
  View,
  ScrollView,
  Button,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { THEME } from "../THEME";
import {
  keyEedit,
  textEdit,
  encriptTextEdit,
  deEncriptTextEdit,
} from "../store/actions/main";
import { lang } from "../lang/lang";

export const MainScreen = () => {
  console.log("start pro - ");
  const currentLang = useSelector((state) => state.mainReducer.askiiCod);
  const dispatch = useDispatch();
  /*useEffect(()=>{
    dispatch(changeKey()
    )
  },[dispatch])
  */
  const mainText = useSelector((state) => state.mainReducer.startText);
  const key = useSelector((state) => state.mainReducer.key);
  const decodingText = useSelector((state) => state.mainReducer.deEncriptText);
  const finishText = useSelector((state) => {
    console.log("store - ", state);
    return state.mainReducer.encriptText;
  });

  //--------------------
  function textToArr(text) {
    let arr = [];
    for (let i = 0; i < text.length; i++) {
      let simbol = text[i];
      let nSimbol = currentLang.indexOf(simbol) + 1;
      arr.push(nSimbol);

      console.log(`array simbol ${nSimbol} - ${text[i]}`);
    }

    return arr;
  }
  function codeText(textSimArray, keySimArray) {
    let i = -1;
    let retArr = textSimArray.map((e) => {
      i++;
      i > keySimArray.length - 1 ? (i = 0) : false;

      let result = (e + keySimArray[i]) % currentLang.length;
      console.log(`text - ${e}, key - ${keySimArray[i]} = result ${result}`);
      return result;
    });

    return retArr;
  }
  function arrToText(arr) {
    let str = "";
    arr.forEach((element) => {
      str = str + currentLang[element - 1];
    });
    return str;
  }
  function deCodeText(textArr, keyArr) {
    let i = -1;
    let long = currentLang.length;
    let result = textArr.map((e) => {
      i++;
      i == keyArr.length ? (i = 0) : false;
      let result = e - keyArr[i];
      result < 0? (result = long - keyArr[i] + e) : false;
       console.log(`${e}-${keyArr[i]}=${result}`)
      console.log(`i = ${i}`)
      console.log(` textArr.length ${textArr.length}`)
      
      return result;
    });
    return result;
  }
  function mainTest(text, key) {
    /*
    - получить допустимые символы 'currentLang'
    - проверить символы в тексте и в ключе
    - перевети симоволы ключа в масив
    - перевести символы текста в масив
    - зашифровать, получить масив шифрованых символов
    - перевести зашифрованный масив в текст
    ---
    - перевеси шифрованый текст в масив
    - перевести ключ в масив (уже сделанно)
    - разшифровать - получить мачив символов
    - перевести масив в текст
    */ 
    let
    //currentLang
      textArray = textToArr(text),
      keyArray = textToArr(key),
      testCodeText = codeText(textArray, keyArray),
      testResultCode = arrToText(testCodeText),
      ///--
      CodeSimbols = textToArr(testResultCode),
      decode = deCodeText(CodeSimbols, keyArray),
      decodetText = arrToText(decode);

    console.log("test text - ", text);
    console.log("test key - ", key);
    console.log("currentLang - ", currentLang);
    console.log("testCodeText", testCodeText);
    console.log("testResultCode", testResultCode);
    console.log("------------------");
    console.log("testDecode symbol", CodeSimbols);
    console.log("testDecodeText", decode);
    console.log("decodetText", decodetText);
  }

  mainTest(`'!"`,`'`)
  mainTest(`hell
  o`,`rty`)






  let onChangeText = (text) => {
    dispatch(textEdit(text));
  };
  let onChangeKey = (text) => {
    dispatch(keyEedit(text));
  };

  let onPressDeCode = () => {
    let textArr = textToArr(mainText); // числовой масив текста
    (keyArr = textToArr(key)), // числовой масив ключа
      (codeArr = deCodeText(textArr, keyArr)), // числовой масив закодированого текста
      (codingText = arrToText(codeArr));
    // dispatch(encriptTextEdit(codingText));
  };
  let onPressCode = () => {
    let textArr = textToArr(mainText), // числовой масив текста
      keyArr = textToArr(key), // числовой масив ключа
      codeArr = codeText(textArr, keyArr), // числовой масив закодированого текста
      codingText = arrToText(codeArr);
    dispatch(encriptTextEdit(codingText));
    codeText(textToArr(currentLang), textToArr(currentLang));

    console.log(textArr);
    console.log(keyArr);
    console.log(codeArr);
    console.log("codingText", codingText);

    let decodeArr = deCodeText(codeArr, keyArr), // числовой масив закодированого текста
      changeText = arrToText(decodeArr);
    dispatch(deEncriptTextEdit(changeText));
  };

  return (
    <View style={styles.container}>
      <View style={styles.header} elevation={7}>
        <TouchableOpacity></TouchableOpacity>
      </View>
      <ScrollView style={styles.scroll}>
        <View style={styles.mainWrapper}>
          <View style={styles.inputWrapper}>
            <TextInput
              style={styles.input}
              numberOfLines={4}
              multiline
              placeholder={"Введите текст..."}
              onChangeText={(text) => onChangeText(text)}
            ></TextInput>
          </View>

          <View style={styles.inputWrapper}>
            <TextInput
              style={styles.input}
              placeholder={"Введите ключ..."}
              //value={Key}
              onChangeText={(text) => onChangeKey(text)}
            ></TextInput>
          </View>
          <View style={styles.btnWrapper}>
            <View style={styles.btn}>
              <Button
                title="Шифровать"
                onPress={onPressCode}
                color={THEME.btn_color}
              ></Button>
            </View>
            <View style={styles.btn}>
              <Button
                title="Разшифровать"
                onPress={onPressDeCode}
                color={THEME.btn_color}
              ></Button>
            </View>
          </View>
          <View style={styles.inputWrapper}>
            <TextInput
              style={styles.input}
              numberOfLines={4}
              multiline
              placeholder={"Результат..."}
              editable={false}
              value={finishText}
            ></TextInput>
          </View>
          <View style={styles.btn}>
            <Button
              title="Копировать"
              onPress={Clipboard.setString(finishText)}
              color={THEME.btn_color}
            ></Button>
          </View>
          <View
            style={{ ...styles.inputWrapper, backgroundColor: THEME.bg_color }}
          >
            <TextInput
              style={{ ...styles.input, backgroundColor: THEME.bg_color }}
              numberOfLines={4}
              multiline
              editable={false}
              placeholder={"Проверка"}
              value={decodingText}
            ></TextInput>
          </View>
        </View>
      </ScrollView>

      <Text>{currentLang}</Text>
      <Text> sdf</Text>
      <StatusBar style="auto" />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: THEME.bg_color,
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  header: {
    width: "100%",
    height: 80,
    backgroundColor: THEME.header_color,
    marginBottom: 5,
  },
  scroll: {
    width: "100%",
  },
  mainWrapper: {
    alignItems: "center",
    backgroundColor: "#fff",
    width: "100%",
    backgroundColor: THEME.bg_color,

    // backgroundColor:'white',
  },
  input: {
    backgroundColor: "#fff",
    width: "100%",
  },
  inputWrapper: {
    padding: 5,
    borderRadius: 8,
    backgroundColor: "#fff",
    width: "80%",
    marginVertical: 5,
  },
  btnWrapper: {
    flexDirection: "row",

    justifyContent: "space-around",
    width: "80%",
    marginTop: 10,
  },
  btn: {
    //alignItems: "flex-end",

    width: "45%",
  },
  text: {
    color: "white",
  },
});

let changeLangArr = (text) => {
  let mapArray = [];
  for (let i = 0; i < str.length; i++) {
    let arr = { i, str: str[i] };
    mapArray.push(arr);
  }
  console.log(mapArray);
  console.log(str);
};
/*
